import { useForm } from '@formspree/react';

const Contact = () => {

    return (
        <div className="contact">
            <h1 className="contactHeader">Contact Us</h1>
            <div className="formContainer">
                <form action="https://formspree.io/f/xrbzzzjg" method="POST">
                    <input type="text" name="name" id="name" placeholder="Name" required />
                    <input type="email" name="email" id="email" placeholder="Email" required />
=                    <input type="text" name="phone" id="phone" placeholder="Phone (123-456-7890)" required />
                    <textarea name="message" id="message" rows="4" placeholder="How Can We Help? Let us know your location and inquiry." required />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>
    );
};

export default Contact;
