import { useForm, ValidationError } from '@formspree/react';
function Contact() {
    const [state, handleSubmit] = useForm("xrbzzzjg");
    if (state.succeeded) {
        return <p>Thanks for emailing!</p>;
    }
    return (

     <div className="formContainer">   
      <h1 className="contactHeader">Contact Us</h1>
      <form onSubmit={handleSubmit}>
              <label htmlFor="email">
                Email Address
              </label>
              <input
                id="email"
                type="email" 
                name="email"
                value={state.values.email || ''}
                onChange={(e) => state.setValue('email', e.target.value)}
              />
              <ValidationError 
                prefix="Email" 
                field="email"
                errors={state.errors}
              />
              <label htmlFor="message">
                Message
              </label>
              <textarea
                id="message"
                name="message"
                value={state.values.message || ''}
                onChange={(e) => state.setValue('message', e.target.value)}
              />
              <ValidationError 
                prefix="Message" 
                field="message"
                errors={state.errors}
              />

              <label htmlFor="phoneNum">
                Phone Number
              </label>
              <textarea
              id="phoneNum"
              name="phoneNum"
              value={state.values.phoneNum || ''}
              onChange={(e) => state.setValue('phoneNum', e.target.value)}
              />
              <ValidationError 
                prefix="Phone Number" 
                field="phoneNum"
                errors={state.errors}
              />
              <button type="submit" disabled={state.submitting}>
                Submit
              </button>
            </form>
     </div>

    );
  }
export default Contact;
