import React from 'react';
import { useForm } from '@formspree/react';

const Contact = () => {
    const [state, handleSubmit] = useForm("xrbzzzjg");
    if (state.succeeded) {
        return <p>Thanks for contacting us!</p>;
    }
    return (
        <div className="contact">
            <h1 className="contactHeader">Contact Us</h1>
            <div className="formContainer">
                <form onSubmit={handleSubmit}>
                    <input type="text" name="name" id="name" placeholder="Name" required />
                    <ValidationError prefix="Name" field="name" errors={state.errors} />
                    <input type="email" name="email" id="email" placeholder="Email" required />
                    <ValidationError prefix="Email" field="email" errors={state.errors} />
                    <input type="text" name="phone" id="phone" placeholder="Phone (123-456-7890)" required />
                    <ValidationError prefix="Phone" field="phone" errors={state.errors} />
                    <textarea name="message" id="message" rows="4" placeholder="How Can We Help? Let us know your location and inquiry." required />
                    <ValidationError prefix="Message" field="message" errors={state.errors} />
                    <button type="submit">Submit</button>
                </form>
            </div>
            <div className="icon">
                <a target="_blank" href="https://www.facebook.com/gctile/">
                    <i className="fa-brands fa-facebook fa-3x"></i>
                </a>
                <a target="_blank" href="https://www.instagram.com/gaglioceramictile/">
                    <i className="fa-brands fa-square-instagram fa-3x"></i>
                </a>
            </div>
        </div>
    );
};

export default Contact;
